import json

def pretty_print_json(json_text):
    try:
        # JSON 텍스트를 파이썬 객체로 로드
        json_obj = json.loads(json_text)

        # 들여쓰기와 정렬 적용하여 예쁘게 출력
        pretty_json = json.dumps(json_obj, indent=2, sort_keys=True)

        print(pretty_json)

    except json.JSONDecodeError as e:
        print(f"Error decoding JSON: {e}")

